query:
SELECT corr(b.total_assets, c.total_car) AS correlation_coefficient
FROM balance_sheet b
JOIN capital_adequacy_ratio c ON b.institution_id = c.institution_id AND b.report_date = c.report_date;

SELECT corr(l.net_stable_funding_ratio, c.core_tier_1_ratio) AS correlation
FROM liquidity_ratio l
JOIN capital_adequacy_ratio c ON l.institution_id = c.institution_id AND l.report_date = c.report_date;

SELECT corr(market_risk_exposure / total_risk_exposure, non_interest_income / (net_interest_income + non_interest_income)) AS correlation
FROM risk_exposure
JOIN income_statement USING (institution_id, report_date);
