SELECT e.name, p.rating FROM employees e JOIN attendance_records a ON e.employee_id = a.employee_id JOIN performance_evaluations p ON e.employee_id = p.employee_id WHERE e.hire_date BETWEEN '2023-01-01' AND '2023-12-31' AND a.date = '2024-09-26';
SELECT COUNT(*) FROM attendance_records WHERE employee_id = 'H05598' AND strftime('%Y', date) = '2024' AND check_out < '18:00';
SELECT e.name, d.name FROM attendance_records ar JOIN employees e ON ar.employee_id = e.employee_id JOIN departments d ON e.department_id = d.department_id WHERE ar.date = '2024-09-28' AND ar.check_in > '09:00';
SELECT e.name FROM employees e JOIN departments d ON e.department_id = d.department_id JOIN attendance_records a ON e.employee_id = a.employee_id WHERE d.name = '运营部' AND a.date = '2024-10-12' AND a.check_in > '09:00';
SELECT e.name, d.name FROM attendance_records a JOIN employees e ON a.employee_id = e.employee_id JOIN departments d ON e.department_id = d.department_id WHERE a.date = '2024-10-11' AND a.check_in > '09:00';
SELECT e.name FROM employees e JOIN departments d ON e.department_id = d.department_id JOIN attendance_records a ON e.employee_id = a.employee_id WHERE d.name = 'Sales' AND a.date = '2024-09-21' AND a.check_in > '09:00';
SELECT e.name, d.name FROM performance_evaluations p JOIN employees e ON p.employee_id = e.employee_id JOIN departments d ON e.department_id = d.department_id WHERE p.evaluation_date = '2023-12-11' AND p.rating = 'C';
SELECT e.name, d.name FROM attendance_records ar JOIN employees e ON ar.employee_id = e.employee_id JOIN departments d ON e.department_id = d.department_id WHERE ar.date = '2024-09-22' AND ar.check_in > '09:00';
SELECT e.name FROM employees e JOIN awards a ON e.employee_id = a.employee_id JOIN performance_evaluations p ON e.employee_id = p.employee_id WHERE a.year = 2023 AND a.award_name = 'Innovation Award' AND p.rating = 'A+';
SELECT e.name FROM employees e JOIN attendance_records a ON e.employee_id = a.employee_id JOIN departments d ON e.department_id = d.department_id WHERE a.date = '2024-10-09' AND d.name = 'Customer Service' AND a.check_in > '08:30:00';
SELECT COUNT(DISTINCT a.employee_id) FROM awards a JOIN probation_employees p ON a.employee_id = p.employee_id WHERE a.year = 2023 AND a.award_name = 'Excellent Employee Award' AND p.start_date <= '2023-12-31' AND p.end_date >= '2023-01-01';
SELECT DISTINCT e.employee_id, e.name FROM employees e JOIN awards a ON e.employee_id = a.employee_id WHERE strftime('%Y', e.hire_date) = '2021' AND a.year = 2022;
SELECT employee_id FROM attendance_records GROUP BY employee_id, date HAVING COUNT(*) > 1;
SELECT DISTINCT d.name FROM departments d JOIN employees e ON d.department_id = e.department_id JOIN attendance_records a ON e.employee_id = a.employee_id WHERE a.date = '2024-09-24' AND a.check_in > '09:30' AND e.remaining_annual_leave < 10;
SELECT e.name FROM employees e JOIN departments d ON e.department_id = d.department_id JOIN attendance_records a ON e.employee_id = a.employee_id WHERE d.name = 'Sales' AND a.date = '2024-09-19' AND a.check_in > '09:00';
SELECT DISTINCT d.name FROM departments d JOIN employees e ON d.department_id = e.department_id JOIN attendance_records a ON e.employee_id = a.employee_id JOIN performance_evaluations p ON e.employee_id = p.employee_id WHERE a.date = '2024-09-26' AND a.check_in > '09:00' AND p.rating = 'C';
SELECT e.name FROM employees e JOIN awards a ON e.employee_id = a.employee_id JOIN performance_evaluations p ON e.employee_id = p.employee_id WHERE YEAR(e.hire_date) = 2021 AND a.year = 2021 AND p.rating = 'B';
SELECT e.name FROM employees e JOIN departments d ON e.department_id = d.department_id JOIN attendance_records a ON e.employee_id = a.employee_id WHERE d.name = 'Sales' AND a.date = '2024-10-03' AND a.check_in > '09:00';
SELECT e.name FROM employees e JOIN awards a ON e.employee_id = a.employee_id JOIN probation_employees p ON e.employee_id = p.employee_id WHERE a.year = 2020 AND a.award_name = '优秀员工' AND p.status = '试用中';
SELECT e.employee_id FROM employees e JOIN awards a ON e.employee_id = a.employee_id JOIN performance_evaluations p ON e.employee_id = p.employee_id WHERE YEAR(e.hire_date) = 2023 AND a.year = 2023 AND p.rating = 'A+';